	time.Sleep(time.Second) // 假设写操作耗时10毫秒
	rwlock.Unlock()         // 解写锁
	wg.Done()
}

func read() {
	rwlock.RLock()               // 加读锁
	time.Sleep(time.Millisecond) // 假设读操作耗时1毫秒
	rwlock.RUnlock()             // 解读锁
	wg.Done()
}

func main() {
	start := time.Now()
	for i := 0; i < 5; i++ {
		wg.Add(1)
		go write()
	}

	for i := 0; i < 1000; i++ {
		wg.Add(1)
		go read()
	}

	wg.Wait()
	end := time.Now()
	fmt.Println(end.Sub(start))
}
```

## 并发安全的sync.Map
- map在高并发下同时读取是不安全的，需要使用 sync.Map

```go
package main
import (
	"fmt"
	"strconv"
	"sync"
)

// 并发安全的map    
var m sync.Map
var wg sync.WaitGroup

func main() {
	for i := 0; i < 20; i++ {
		wg.Add(1)
		go func(n int) {
			key := strconv.Itoa(n)
			// 向map中写入值 Store(key, value)
			m.Store(key, n) 
			// 从map中取值 value, err = Load(key)
			// Load传入key，可以得到对应的value，如果取值失败，则返回err，此时value = nil
			value, _ := m.Load(key)
			fmt.Println(value)
			wg.Done()
		}(i)
	}
	wg.Wait()
}
